mapper.insert:
/*Board - boardInsert */
SELECT spring_board_seq.nextval FROM dual
INSERT INTO spring_board( b_num, b_name, b_title, b_content, b_pwd ) VALUES( #{b_num}, #{b_name}, #{b_title}, #{b_content}, #{b_pwd} )

mapper.select:
/*Board - pwdConfirm */
SELECT NVL((
	SELECT 1
	FROM spring_board
	WHERE b_num =#{b_num}
	AND b_pwd =#{b_pwd}
),0)as state
from dual

mapper.update:
/*Board - boardUpdate */
UPDATE spring_board
	SET b_title =#{b_title},
		b_content =#{b_content},
		b_date =sysdate

		,b_pwd =#{b_pwd}

	WHERE b_num =#{b_num}

mapper.delete:
/* Board - boardDelete */
DELETE
FROM spring_board
WHERE b_num =#{b_num}
